mapper.select:
select id,tag_id,level,msg,name,code,position,timestamp,state,type,warning_code,remark from pr_tag_warning_status where area_code=#{areaCode} and op=1 and level is not null and type =3 and position!="未知位置"
and state =#{state}
and name like '%${name}%'
and code like '%${code}%'
and msg like '%${msg}%'
and timestamp BETWEEN #{startTime,jdbcType=BIGINT} and #{endTime,jdbcType=BIGINT}
order by
${sort}
${order}
timestamp desc, state desc

select level,count(level) as amount from pr_tag_warning_status where area_code=#{areaCode} and level is not null and op=1 and type =3 and position!="未知位置"
and timestamp BETWEEN #{startTime,jdbcType=BIGINT} and #{endTime,jdbcType=BIGINT}
group by level

select
id,name,code,msg,tag_id,level,warning_code,position,state,type,timestamp,remark
from pr_tag_warning_status
where code = #{code} and level is not null and position!="未知位置" and type =3 and op=1

	and state =#{state}


	and msg like '%${msg}%'


	and timestamp BETWEEN #{startTime,jdbcType=BIGINT} and
	#{endTime,jdbcType=BIGINT}

order by


		${sort}

			${order}



		timestamp desc, state desc



	limit #{limit}

select id,tag_id,level,msg,name,code,position,timestamp,state,type,warning_code,remark from pr_tag_warning_status where area_code=#{areaCode} and (op=1 and warning_code in ("0101","0102","0202")) and type =3 and level is not null and position!="未知位置"
and state =#{state}
and name like '%${name}%'
and msg like '%${msg}%'
and code like '%${code}%'
and timestamp BETWEEN #{startTime,jdbcType=BIGINT} and #{endTime,jdbcType=BIGINT}
order by
${sort}
${order}
timestamp desc, state desc

select
id,tag_id,level,msg,name,code,position,timestamp,state,type,warning_code,remark
from pr_tag_warning_status
where 1=1 and op=1 and type =3 and level is not null and position!="未知位置"

	and id =#{id,jdbcType=BIGINT}



	and state =#{state}



	and level =#{level}



	and name like '%${name}%'



	and code like '%${code}%'



	and position like '%${position}%'



	and msg like '%${msg}%'



	and pos_code = #{areaCode}



	and pos_code regexp '^${floorUniqueCode}.'



	and pos_code regexp '^${buildingUniqueCode}.'



	and pos_code regexp '^${domainUniqueCode}.'



	and timestamp BETWEEN #{startTime,jdbcType=BIGINT}
	and #{endTime,jdbcType=BIGINT}

order by


		${sort}

			${order}



		timestamp desc, state desc
